+        // ======== MOBILE-SPECIFIC FEATURES ========
+        function initializeMobileFeatures() {
+            // Detect mobile device
+            const isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
+            
+            if (isMobile) {
+                // Add mobile-specific classes
+                document.body.classList.add('mobile-device');
+                
+                // Prevent iOS bounce effect
+                document.addEventListener('touchmove', function(e) {
+                    if (e.target.closest('.scrollable-container') || 
+                        e.target.closest('.enhanced-sales-container') ||
+                        e.target.closest('.enhanced-purchase-container')) {
+                        return; // Allow scrolling in designated areas
+                    }
+                    e.preventDefault();
+                }, { passive: false });
+
+                // Handle virtual keyboard
+                handleVirtualKeyboard();
+                
+                // Add touch-friendly interactions
+                addTouchFriendlyInteractions();
+            }
+
+            // Add orientation change handler
+            window.addEventListener('orientationchange', function() {
+                setTimeout(function() {
+                    window.scrollTo(0, 0);
+                    adjustForOrientation();
+                }, 100);
+            });
+
+            // Handle safe area insets for newer phones
+            if (CSS.supports('padding-top: env(safe-area-inset-top)')) {
+                document.documentElement.style.setProperty('--safe-area-inset-top', 'env(safe-area-inset-top)');
+                document.documentElement.style.setProperty('--safe-area-inset-bottom', 'env(safe-area-inset-bottom)');
+            }
+        }
+
+        function handleVirtualKeyboard() {
+            let viewportHeight = window.innerHeight;
+            
+            // Detect when virtual keyboard is shown/hidden
+            window.addEventListener('resize', function() {
+                const currentHeight = window.innerHeight;
+                const heightDifference = viewportHeight - currentHeight;
+                
+                if (heightDifference > 150) { // Keyboard is likely open
+                    document.body.classList.add('keyboard-open');
+                } else {
+                    document.body.classList.remove('keyboard-open');
+                }
+            });
+
+            // Focus handling for input fields
+            const inputFields = document.querySelectorAll('input, textarea, select');
+            inputFields.forEach(field => {
+                field.addEventListener('focus', function() {
+                    setTimeout(() => {
+                        this.scrollIntoView({ behavior: 'smooth', block: 'center' });
+                    }, 300);
+                });
+            });
+        }
+
+        function addTouchFriendlyInteractions() {
+            // Add ripple effect to buttons
+            const buttons = document.querySelectorAll('.btn, .tab');
+            buttons.forEach(button => {
+                button.addEventListener('touchstart', function(e) {
+                    const ripple = document.createElement('span');
+                    ripple.classList.add('ripple');
+                    this.appendChild(ripple);
+
+                    const rect = this.getBoundingClientRect();
+                    const size = Math.max(rect.width, rect.height);
+                    const x = e.touches[0].clientX - rect.left - size / 2;
+                    const y = e.touches[0].clientY - rect.top - size / 2;
+
+                    ripple.style.width = ripple.style.height = size + 'px';
+                    ripple.style.left = x + 'px';
+                    ripple.style.top = y + 'px';
+
+                    setTimeout(() => {
+                        ripple.remove();
+                    }, 600);
+                });
+            });
+
+            // Improve scrolling performance
+            const scrollableElements = document.querySelectorAll('.scrollable-container, .enhanced-sales-container, .enhanced-purchase-container');
+            scrollableElements.forEach(element => {
+                element.style.webkitOverflowScrolling = 'touch';
+                element.style.overflowScrolling = 'touch';
+            });
+        }
+
+        function adjustForOrientation() {
+            const isLandscape = window.innerHeight < window.innerWidth;
+            
+            if (isLandscape && window.innerHeight < 500) {
+                document.body.classList.add('landscape-mode');
+                // Reduce header size in landscape
+                const header = document.querySelector('.header');
+                if (header) {
+                    header.style.padding = '10px 15px';
+                }
+            } else {
+                document.body.classList.remove('landscape-mode');
+                // Restore header size
+                const header = document.querySelector('.header');
+                if (header) {
+                    header.style.padding = '';
+                }
+            }
+        }
+
+        // Enhanced mobile alert system
+        function showMobileAlert(title, message, callback) {
+            const isMobile = window.innerWidth <= 768;
+            
+            if (isMobile) {
+                const modal = document.getElementById('mobile-modal');
+                const header = document.getElementById('mobile-modal-header');
+                const body = document.getElementById('mobile-modal-body');
+                const okButton = document.getElementById('mobile-modal-ok');
+
+                header.textContent = title;
+                body.innerHTML = message;
+                modal.classList.add('show');
+
+                okButton.onclick = function() {
+                    modal.classList.remove('show');
+                    if (callback) callback();
+                };
+
+                // Close on background click
+                modal.onclick = function(e) {
+                    if (e.target === modal) {
+                        modal.classList.remove('show');
+                        if (callback) callback();
+                    }
+                };
+            } else {
+                // Fallback to regular alert for desktop
+                alert(title + '\n\n' + message);
+                if (callback) callback();
+            }
+        }
+
+        // Performance optimizations for mobile
+        function optimizeForMobile() {
+            // Debounce scroll events
+            let ticking = false;
+            function updateScrollPosition() {
+                // Handle scroll-based animations
+                ticking = false;
+            }
+
+            document.addEventListener('scroll', function() {
+                if (!ticking) {
+                    requestAnimationFrame(updateScrollPosition);
+                    ticking = true;
+                }
+            }, { passive: true });
+
+            // Lazy load images if any
+            const images = document.querySelectorAll('img[data-src]');
+            if (images.length > 0) {
+                const imageObserver = new IntersectionObserver((entries) => {
+                    entries.forEach(entry => {
+                        if (entry.isIntersecting) {
+                            const img = entry.target;
+                            img.src = img.dataset.src;
+                            img.classList.remove('lazy');
+                            imageObserver.unobserve(img);
+                        }
+                    });
+                });
+
+                images.forEach(img => imageObserver.observe(img));
+            }
+        }
+
+        // Call mobile optimizations
+        if (window.innerWidth <= 768) {
+            optimizeForMobile();
+        }
