+                // Wait for the user to respond to the prompt
+                deferredPrompt.userChoice.then((choiceResult) => {
+                    if (choiceResult.outcome === 'accepted') {
+                        console.log('User accepted the install prompt');
+                    } else {
+                        console.log('User dismissed the install prompt');
+                    }
+                    deferredPrompt = null;
+                    dismissInstallPrompt();
+                });
+            }
+        }
+
+        function dismissInstallPrompt() {
+            const banner = document.getElementById('install-banner');
+            if (banner) {
+                banner.remove();
+                document.body.style.paddingTop = '';
+            }
+        }
+
+        // Handle app installed event
+        window.addEventListener('appinstalled', function(evt) {
+            console.log('App was installed');
+            dismissInstallPrompt();
+            showMobileAlert('Success!', 'Theia Jewelz has been installed successfully!');
+        });
+
+        // ======== NETWORK STATUS MONITORING ========
+        function handleOnline() {
+            console.log('Back online');
+            document.body.classList.remove('offline');
+            showToast('Connected', 'Back online! Data will sync automatically.', 'success');
+        }
+
+        function handleOffline() {
+            console.log('Gone offline');
+            document.body.classList.add('offline');
+            showToast('Offline', 'You are offline. Data will be saved locally.', 'warning');
+        }
+
+        window.addEventListener('online', handleOnline);
+        window.addEventListener('offline', handleOffline);
+
+        // Check initial network status
+        if (!navigator.onLine) {
+            handleOffline();
+        }
     </script>

