    <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/xlsx/0.17.0/xlsx.full.min.js"></script>
    <script>
        class JewelrySalesTracker {
            constructor() {
                this.sales = JSON.parse(localStorage.getItem('jewelrySales')) || [];
                this.form = document.getElementById('salesForm');
                this.recordsList = document.getElementById('recordsList');
                this.totalSalesElement = document.getElementById('totalSales');
                this.salesChart = null;

                this.init();
            }

            init() {
                // Set today's date as default
                document.getElementById('date').valueAsDate = new Date();

                // Bind events
                this.form.addEventListener('submit', (e) => this.handleSubmit(e));
                document.getElementById('downloadExcel').addEventListener('click', () => this.downloadExcel());

                // Tab switching
                document.querySelectorAll('.tab').forEach(tab => {
                    tab.addEventListener('click', (e) => this.switchTab(e.target.dataset.tab));
                });

                // Render initial records and chart
                this.renderRecords();
                this.updateTotalSales();
                this.renderChart();
            }

            switchTab(tabId) {
                document.querySelectorAll('.tab').forEach(tab => tab.classList.remove('active'));
                document.querySelectorAll('.tab-content').forEach(content => content.classList.remove('active'));

                document.querySelector(`.tab[data-tab="${tabId}"]`).classList.add('active');
                document.getElementById(tabId).classList.add('active');
            }

            renderChart() {
                const ctx = document.getElementById('salesChart').getContext('2d');
                const labels = this.sales.map(sale => this.formatDate(sale.date));
                const data = this.sales.map(sale => sale.price);

                if (this.salesChart) {
                    this.salesChart.destroy();
                }

                this.salesChart = new Chart(ctx, {
                    type: 'line',
                    data: {
                        labels: labels,
                        datasets: [{
                            label: 'Sales',
                            data: data,
                            borderColor: '#667eea',
                            fill: false
                        }]
                    },
                    options: {
                        responsive: true,
                        scales: {
                            x: { display: true },
                            y: { display: true }
                        }
                    }
                });
            }

            downloadExcel() {
                const worksheet = XLSX.utils.json_to_sheet(this.sales);
                const workbook = XLSX.utils.book_new();
                XLSX.utils.book_append_sheet(workbook, worksheet, "Sales");
                XLSX.writeFile(workbook, "JewelrySales.xlsx");
            }

            async handleSubmit(e) {
                e.preventDefault();

                const formData = new FormData(this.form);
                const saleData = {
                    id: Date.now(),
                    itemName: formData.get('itemName') || document.getElementById('itemName').value,
                    category: document.getElementById('category').value,
                    price: parseFloat(document.getElementById('price').value),
                    date: document.getElementById('date').value,
                    customer: document.getElementById('customer').value,
                    phone: document.getElementById('phone').value,
                    timestamp: new Date().toISOString()
                };

                // Save locally
                this.addSale(saleData);

                this.form.reset();
                document.getElementById('date').valueAsDate = new Date();

                // Focus back to first input for quick entry
                document.getElementById('itemName').focus();
            }

            addSale(saleData) {
                this.sales.unshift(saleData); // Add to beginning for newest first
                this.saveSales();
                this.renderRecords();
                this.updateTotalSales();
            }

            deleteSale(id) {
                if (confirm('Are you sure you want to delete this sale record?')) {
                    this.sales = this.sales.filter(sale => sale.id !== id);
                    this.saveSales();
                    this.renderRecords();
                    this.updateTotalSales();
                }
            }

            saveSales() {
                localStorage.setItem('jewelrySales', JSON.stringify(this.sales));
            }

            updateTotalSales() {
                const total = this.sales.reduce((sum, sale) => sum + sale.price, 0);
                this.totalSalesElement.textContent = `‚Çπ${total.toLocaleString('en-IN', {minimumFractionDigits: 2, maximumFractionDigits: 2})}`;
            }

            formatDate(dateString) {
                const date = new Date(dateString);
                return date.toLocaleDateString('en-IN', {
                    day: '2-digit',
                    month: 'short',
                    year: 'numeric'
                });
            }

            renderRecords() {
                if (this.sales.length === 0) {
                    this.recordsList.innerHTML = `
                        <div class="no-records">
                            No sales records yet.<br>
                            Add your first sale above!
                        </div>
                    `;
                    return;
                }

                this.recordsList.innerHTML = this.sales.map(sale => `
                    <div class="record-card fade-in">
                        <button class="delete-btn" onclick="tracker.deleteSale(${sale.id})" title="Delete Record">
                            √ó
                        </button>
                        <div class="record-header">
                            <div class="item-name">${sale.itemName}</div>
                            <div class="price">‚Çπ${sale.price.toLocaleString('en-IN', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</div>
                        </div>
                        <div class="record-details">
                            <div class="detail-item">
                                <span class="detail-label">Category:</span>
                                <span class="category-badge category-${sale.category}">
                                    ${sale.category.charAt(0).toUpperCase() + sale.category.slice(1)}
                                </span>
                            </div>
                            <div class="detail-item">
                                <span class="detail-label">Date:</span>
                                <span class="detail-value">${this.formatDate(sale.date)}</span>
                            </div>
                            <div class="detail-item">
                                <span class="detail-label">Customer:</span>
                                <span class="detail-value">${sale.customer}</span>
                            </div>
                            ${sale.phone ? `
                            <div class="detail-item">
                                <span class="detail-label">Phone:</span>
                                <span class="detail-value">${sale.phone}</span>
                            </div>` : ''}
                        </div>
                    </div>
                `).join('');
            }
        }

        const tracker = new JewelrySalesTracker();
    </script>
</body>
</html>
