+                            phone: customerPhone,
+                            address: customerAddress,
+                            email: customerEmail,
+                            createdAt: new Date().toISOString()
+                        };
+                        
+                        customersData.push(newCustomer);
+                        importedCount++;
+                    }
+                    
+                    // Save to localStorage
